    private string url
    {
        get
        {
            string ret = String.Empty;

            if (_blog.Id < 1)
            {
                ret = String.Concat(baseUrl, "/Create").Replace("//", "/");

                return ret;
            }
            ret = String.Concat(baseUrl, "/Edit/", _blog.Id.ToString()).Replace("//", "/");

            return ret;
        }

    }

